    justifyContent: "center",
    alignItems: "center"
  },
  likes:{
    justifyContent: "center",
    alignItems: "center"
  },
  commentContainer: darkmode=>({
    marginTop: 10,
    marginHorizontal: 10,
    borderBottomWidth:1,
    borderColor: darkmode?"rgba(255,255,255,0.1)":"#dedede",
  }),
  username:{
    marginRight: 10,
    flexDirection:"row",
    alignItems:"center",
    justifyContent:"center"
  },
  delete:{
    alignItems:"center",
    justifyContent:"center",
    paddingVertical: 5,
    paddingHorizontal: 10,
  },
  commentHeader:{
    flexDirection:"row",
    justifyContent:"space-between",
  },
  commentHeaderLeft:{
    flexDirection:"row", 
    alignItems:"center",
    paddingLeft: 10
  },
  formContainer: darkmode=>({
    flex:1,
    flexDirection:"row", 
    borderWidth:0, 
    borderColor:"white", 
    borderRadius:30, 
    paddingHorizontal: 10,
    backgroundColor: darkmode?"rgba(255,255,255,0.1)":"#eeeeee",
    marginHorizontal: 10
  }),
  formInput: textColor=>({
    flex:5,
    paddingRight: 15,
    color: textColor,
    paddingVertical: 5,
    fontSize: 20,
    marginVertical: 5,
  }),
  formSubmit: textColor=>({
    flex:1, 
    alignItems:"center", 
    justifyContent:"center", 
    backgroundColor: BUTTON_COLOR,
    marginLeft: 10,
    marginVertical : 10,
    borderRadius: 20
  }),
  tagWrapper:{
    borderWidth: 0,
    borderRadius: 15,
    paddingVertical: 5,
    paddingHorizontal: 10,
    marginLeft: 5,
    marginBottom: 15,
    backgroundColor: "#eeeeee",
  },
