import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { StyleSheet, useWindowDimensions, View } from 'react-native';
import { useColorScheme } from 'react-native-appearance';
import { SEARCH_TAG } from '../queries';
import {Ionicons} from '@expo/vector-icons';
import DismissKeyboard from '../components/DismissKeyboard';


export default function EditTags({navigation}){
  const {width, height} = useWindowDimensions();
  const {setValue, register ,watch} = useForm();
  const [selection, setSelection] = useState("tag");
  const colorScheme = useColorScheme();
  const darkmode = colorScheme==="dark";

  //query
  console.log("selection:",selection);
  const SearchBox = () => (
    <View style={CSS.searchBoxContainer}>      
      <View style={{
        flexDirection:"row",
        justifyContent:"center",
        alignItems:"center",
        marginTop: 10,
        paddingHorizontal: 3,
        paddingVertical: 5,
        width:width*0.8,
        borderRadius: 10,
        backgroundColor:darkmode?"rgba(255,255,255,0.1)":colors.skin
      }}>
        <Ionicons name="search" size={28} color="#ababab"/>
        <TextInput 
          style={{
            borderWidth:0, 
            fontSize:18,
            paddingHorizontal: 5,
            paddingVertical: 7,
            borderRadius: 9,
            width: "90%",
            color: darkmode?"white":"black"
          }} 
          placeholderTextColor={darkmode?"rgba(255,255,255,0.5)":"#ababab"} 
          placeholder="Search"
          autoCaptialize="none"      
          returnKeyType="search"
          autoCorrect={false}
          onChangeText={(text)=>setValue("keyword", text)}
        />
      </View>
    </View>
  );
  useEffect(()=>{
    navigation.setOptions({
      headerTitle: SearchBox,

    });
    register("keyword");
  },[]);

  console.log(watch());
  return(
    <DismissKeyboard>
      <View style={{
        backgroundColor:colorScheme==="dark"?"black":"white", 
        flex:1, 
        alignItems:"center",
        justifyContent: "center",
      }}>
      </View>     
    </DismissKeyboard>
  )
}

const CSS = StyleSheet.create({
  tagContainer:darkmode=>({
    borderRadius: 25,
    paddingHorizontal: 2,
    paddingVertical: 2,
    marginHorizontal: 50,
    marginTop: 5,
    flex:1,
    justifyContent:"center",
    backgroundColor:darkmode?"rgba(255,255,255,0.1)":"#74D19D",
  }),
  tagLeft:{
    flexDirection:"row",
    paddingLeft: 10,
    paddingVertical: 5,
    alignItems:"center"
  },
  tagText:{
    color: "white",
    fontSize: 18,
    fontWeight: "bold"
  },
  searchBoxContainer:{
    flex:1,
    width:"100%",
    borderColor:"black",
    borderWidth:0,
    alignItems:"center",
    justifyContent:"center",
  }
})
